import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Converter() {
  const [currencies, setCurrencies] = useState([]);
  const [from, setFrom] = useState('USD');
  const [to, setTo] = useState('PKR');
  const [amount, setAmount] = useState(1);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const fetchCurrencies = async () => {
    const res = await axios.get('http://localhost:5000/api/currencies');
    setCurrencies(Object.keys(res.data.data));
  };

  const convert = async () => {
    setLoading(true);
    const res = await axios.get('http://localhost:5000/api/convert', {
      params: { base_currency: from, currencies: to }
    });
    const rate = res.data.data[to];
    const final = rate * amount;
    setResult(final.toFixed(2));
    saveHistory({ from, to, amount, result: final.toFixed(2), date: new Date().toLocaleString() });
    setLoading(false);
  };

  const saveHistory = (entry) => {
    const old = JSON.parse(localStorage.getItem('history') || '[]');
    localStorage.setItem('history', JSON.stringify([entry, ...old]));
  };

  useEffect(() => {
    fetchCurrencies();
  }, []);

  return (
    <div className="p-3">
      <h4>Currency Converter</h4>
      <input type="number" value={amount} onChange={e => setAmount(e.target.value)} />
      <select value={from} onChange={e => setFrom(e.target.value)}>
        {currencies.map(c => <option key={c}>{c}</option>)}
      </select>
      <span> to </span>
      <select value={to} onChange={e => setTo(e.target.value)}>
        {currencies.map(c => <option key={c}>{c}</option>)}
      </select>
      <button className="btn btn-primary mt-2" onClick={convert}>
        {loading ? "Converting..." : "Convert"}
      </button>
      {result && <h5 className="mt-3">Result: {result} {to}</h5>}
    </div>
  );
}

export default Converter;
